import "./ServiceNous.css";
import { useState } from "react";
import map from "../../Images/map"
function ServiceNous() {
    const [showText1, setShowText1] = useState(false);
    const [showText2, setShowText2] = useState(false);
    const [showText3, setShowText3] = useState(false);

    const handleShowText1 = () => {
        setShowText1(!showText1);
    };

    const handleShowText2 = () => {
        setShowText2(!showText2);
    };

    const handleShowText3 = () => {
        setShowText3(!showText3);
    };

    return (
        <div className="col-md-3" style={{ paddingBottom: '60px' }}>
            <img src="../Images/map.png" alt="Map" />
            <p style={{ fontSize: 'x-large', fontFamily: "'Franklin Gothic Medium', 'Arial Narrow', Arial, sans-serif" }}>Moyen de transport</p>

            <p className="lead text-secondary" style={{ textAlign: 'justify' }}>
                Notre association fournir des multiples moyens de transport tel que les 
                <span id="doc1">{showText1 ? '' : 'bus ou bien les mini bus aussi comme nous sommes une association on a le droit de profiter d\'un remise pour les trains et les meteros notre objectif principal c\'est de prenez soin du confort de notre visiteur.'}
                </span>{showText1 && <span id="more1">bus ou bien les mini bus aussi comme nous sommes une association on a le droit de profiter d'un remise pour les trains et les meteros notre objectif principal c'est de prenez soin du confort de notre visiteur.</span>}</p>
           
            <button id="btn1" onClick={handleShowText1}>{showText1 ? 'Lire moins' : 'Lire la suite'}</button>
           
            <p className="lead text-secondary" style={{ textAlign: 'justify' }}>
                Notre association fournir des multiples moyens de transport tel que les 
                <span id="doc2">{showText2 ? '' : 'taxis, les voitures de location, les motos et les vélos.'}</span>{showText2 && <span id="more2">taxis, les voitures de location, les motos et les vélos.</span>}</p>
           
            <button id="btn2" onClick={handleShowText2}>{showText2 ? 'Lire moins' : 'Lire la suite'}</button>
           
            <p className="lead text-secondary" style={{ textAlign: 'justify' }}>
                Nous offrons également un service de navette aéroport, qui est disponible pour tous les visiteurs de notre association.</p>
           
            <button id="btn3" onClick={handleShowText3}>{showText3 ? 'Lire moins' : 'Lire la suite'}</button>
        </div>
    );
}


export default ServiceNous;
