import { Link } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import './main.css';
import React, { useState, useEffect } from 'react';

function Main() {
    const [isScrolled, setIsScrolled] = useState(false);

    useEffect(() => {
      function handleScroll() {
        if (window.scrollY > 5) {
          setIsScrolled(true);
        } else {
          setIsScrolled(false);
        }
      }
      window.addEventListener('scroll', handleScroll);
      return () => {
        window.removeEventListener('scroll', handleScroll);
      };
    }, []);
    return (
        <>
            {/* style={{position: 'fixed', width: '100%', zIndex: '1000'}} 
            <nav className={isScrolled ? "navbar animNav" : "navbar"}>*/}
            {isScrolled ? "main " : "main" &&(
            <nav className= "navbar navbar-expand-md navbar-drak" style={{position: 'fixed', width: '100%', zIndex: '1000'}} >
                <div id="logo"> <a href="index.html">{/*<img src="images/logo.png">*/}</a></div>
                <button type="button" className="navbar-toggler bg-light"> <span className="navbar-toggler-icon"
                    data-toggle="collapse" data-target="#nav"></span></button>
                <div className="collapse navbar-collapse justify-content-center" id="nav">
                    <ul className="navbar-nav ">
                        <li className="nav-item ">
                            <Link to="/Acceuil">ACCEUIL</Link>
                        </li>
                        <li className="nav-item dropdown ">
                            <Link to="/Gudie">GUIDE</Link>
                        </li>
                        <li className="nav-item">
                            <Link to="/Destination">DESTINATION</Link>
                        </li>
                        <li className="nav-item">
                            <Link to="/Contact">CONTACT</Link>
                        </li>
                        <form className="form-inline ml-3">
                            <div className="input-group">
                                <input type="text" className="form-control" placeholder="Recherche"></input>
                                <div className="input-group-append">
                                    <button type="button" className="btn bg-light">
                                        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor"
                                            className="bi bi-search" viewBox="0 0 16 16">
                                            <path
                                                d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z" />
                                        </svg>
                                    </button>
                                </div>

                            </div>
                        </form>
                    </ul>
                </div>
            </nav>
            )}
        </>
    );
}

export default Main;
